definition.script:
pipeline {
	agent any
	parameters {
        string(
            name: "PROJECT_NAME",
            description: "Проект для развертывания"
        )
        booleanParam(
            name: "ROLLBACK_MIGRATIONS",
            defaultValue: false,
            description: "Нужно ли очичтить бд?"
        )
        booleanParam(
            name: "CLEAN_KV", 
            defaultValue: false,
            description: "Нужно ли очистить consul kv?"
        )
        booleanParam(
            name: "CLEAN_SD",
            defaultValue: false,
            description: "Нужно ли очистить consul sd?"
        )
        string(
            name: "ANSIBLE_EXTRA_KEYS",
            description: "Дополнительные ключи ansible"
        )
    }
	environment {
	    VAULT_PASS = credentials('ansible-vault-password')
		PREPARE_CMD = "chmod o-w /workspace/ansible && cd /workspace/ansible"
		BASE_ANSIBLE_PARAMS = "-i ../inventory/general -i ../inventory/${params.PROJECT_NAME}/hosts -i ../inventory/${params.PROJECT_NAME}/configuration"
		ANSIBLE_EXEC = "/root/.local/bin/ansible"
		ANSIBLE_EXEC_PLAYBOOK = "/root/.local/bin/ansible-playbook"
		DEPLOY_GROUP = "deploy_db"
	}
	stages {
	    stage("Test connection") {
            steps {
                script {
                    withCredentials([string(credentialsId: 'ansible-vault-password', variable: 'VAULT_PASS')]) {
                      sh """
                        ${env.PREPARE_CMD} && \
                        ${env.ANSIBLE_EXEC} ${env.BASE_ANSIBLE_PARAMS} -m ping ${env.DEPLOY_GROUP} \
                        ${params.ANSIBLE_EXTRA_KEYS} --vault-password-file <(echo "$VAULT_PASS")
                        """
                    }
                }
            }
        }
		stage("Prepare cleaning"){
			parallel{
				stage("Rollback migrations") {
					when {
						expression { params.ROLLBACK_MIGRATIONS }
					}
					steps {
						script {
							 withCredentials([string(credentialsId: 'ansible-vault-password', variable: 'VAULT_PASS')]) {
							  sh """
								${env.PREPARE_CMD} && \
								${env.ANSIBLE_EXEC_PLAYBOOK} ${env.BASE_ANSIBLE_PARAMS} ./cleaning/rollback-migrations.yml \
								${params.ANSIBLE_EXTRA_KEYS} --vault-password-file <(echo "$VAULT_PASS")
								"""
							 }
						}
					}
				}
				stage("Clean Consul KV") {
					when {
						expression { params.CLEAN_KV }
					}
					steps {
						script {
							 withCredentials([string(credentialsId: 'ansible-vault-password', variable: 'VAULT_PASS')]) {
							  sh """
								${env.PREPARE_CMD} && \
								${env.ANSIBLE_EXEC_PLAYBOOK} ${env.BASE_ANSIBLE_PARAMS} ./cleaning/clean-consul-kv.yml \
								${params.ANSIBLE_EXTRA_KEYS} --vault-password-file <(echo "$VAULT_PASS")
								"""
							 }
						}
					}
				}
				stage("Clean Consul SD") {
					when {
						expression { params.CLEAN_SD }
					}
					steps {
						script {
							 withCredentials([string(credentialsId: 'ansible-vault-password', variable: 'VAULT_PASS')]) {
							  sh """
								${env.PREPARE_CMD} && \
								${env.ANSIBLE_EXEC_PLAYBOOK} ${env.BASE_ANSIBLE_PARAMS} ./cleaning/clean-consul-sd.yml \
								${params.ANSIBLE_EXTRA_KEYS} --vault-password-file <(echo "$VAULT_PASS")
								"""
							 }
						}
					}
				}
		}	}
	}
}
