definition.script:
pipeline {
	agent any
	parameters {
        string(
            name: "PROJECT_NAME",
            description: "Проект для развертывания"
        )
        booleanParam(
            name: "BUILD_ACCOUNTS",
            defaultValue: false,
            description: "Нужно ли собрать accounts backend?"
        )
		booleanParam(
            name: "INSTALL_ACCOUNTS",
            defaultValue: false,
            description: "Нужно ли задеплоить accounts backend?"
        )

        string(
            name: "ANSIBLE_EXTRA_KEYS",
            description: "Дополнительные ключи ansible"
        )
    }
	environment {
	    VAULT_PASS = credentials('ansible-vault-password')
		PREPARE_CMD = "chmod o-w /workspace/ansible && cd /workspace/ansible"
		BASE_ANSIBLE_PARAMS = "-i ../inventory/general -i ../inventory/${params.PROJECT_NAME}/hosts -i ../inventory/${params.PROJECT_NAME}/configuration"
		ANSIBLE_EXEC = "/root/.local/bin/ansible"
		ANSIBLE_EXEC_PLAYBOOK = "/root/.local/bin/ansible-playbook"
		DEPLOY_GROUP = "deploy_back"
	}
	stages {
	    stage("Test connection") {
            steps {
                script {
                    withCredentials([string(credentialsId: 'ansible-vault-password', variable: 'VAULT_PASS')]) {
                      sh """
                        ${env.PREPARE_CMD} && \
                        ${env.ANSIBLE_EXEC} ${env.BASE_ANSIBLE_PARAMS} -m ping ${env.DEPLOY_GROUP} \
                        ${params.ANSIBLE_EXTRA_KEYS} --vault-password-file <(echo "$VAULT_PASS")
                        """
                    }
                }
            }
        }
        stage("Prepare builds") {
		    parallel {
                stage("Build accounts") {
                    when {
                        expression { params.BUILD_ACCOUNTS }
                    }
                    steps {
						script {
                             withCredentials([string(credentialsId: 'ansible-vault-password', variable: 'VAULT_PASS')]) {
    						  sh """
    							${env.PREPARE_CMD} && \
    							${env.ANSIBLE_EXEC_PLAYBOOK} ${env.BASE_ANSIBLE_PARAMS} ./backs/build-backend.yml \
    							${params.ANSIBLE_EXTRA_KEYS} --vault-password-file <(echo "$VAULT_PASS") -e "backend_name=iskr-accounts"
    							"""
                             }
    					}
                    }
                }
            }
		}		
		stage("Prepare installations") {
		    parallel {
                stage("Deploy accounts") {
                    when {
                        expression { params.INSTALL_ACCOUNTS }
                    }
                    steps {
						script {
                             withCredentials([string(credentialsId: 'ansible-vault-password', variable: 'VAULT_PASS')]) {
    						  sh """
    							${env.PREPARE_CMD} && \
    							${env.ANSIBLE_EXEC_PLAYBOOK} ${env.BASE_ANSIBLE_PARAMS} ./backs/deploy-accounts.yml \
    							${params.ANSIBLE_EXTRA_KEYS} --vault-password-file <(echo "$VAULT_PASS") 
    							"""
                             }
    					}
                    }
                }
            }
		}		
	}
}
