definition.script:
pipeline {
	agent any
	parameters {
        string(
            name: "PROJECT_NAME",
            description: "Проект для развертывания"
        )
        booleanParam(
            name: "INSTALL_CONSUL_CONFIGURATION",
            defaultValue: false,
            description: "Нужно ли накатить конфигурацию consul?"
        )
        booleanParam(
            name: "INSTALL_KEYCLOAK_CONFIGURATION",
            defaultValue: false,
            description: "Нужно ли накатить конфигурацию keycloak?"
        )
        booleanParam(
            name: "INSTALL_RABBIT_CONFIGURATION",
            defaultValue: false,
            description: "Нужно ли накатить конфигурацию rabbitmq?"
        )
        string(
            name: "ANSIBLE_EXTRA_KEYS",
            description: "Дополнительные ключи ansible"
        )
    }
	environment {
	    VAULT_PASS = credentials('ansible-vault-password')
		PREPARE_CMD = "chmod o-w /workspace/ansible && cd /workspace/ansible"
		BASE_ANSIBLE_PARAMS = "-i ../inventory/general -i ../inventory/${params.PROJECT_NAME}/hosts -i ../inventory/${params.PROJECT_NAME}/configuration"
		ANSIBLE_EXEC = "/root/.local/bin/ansible"
		ANSIBLE_EXEC_PLAYBOOK = "/root/.local/bin/ansible-playbook"
		DEPLOY_GROUP = "deploy_configuration"
	}
	stages {
	    stage("Test connection") {
            steps {
                script {
                    withCredentials([string(credentialsId: 'ansible-vault-password', variable: 'VAULT_PASS')]) {
                      sh """
                        ${env.PREPARE_CMD} && \
                        ${env.ANSIBLE_EXEC} ${env.BASE_ANSIBLE_PARAMS} -m ping ${env.DEPLOY_GROUP} \
                        ${params.ANSIBLE_EXTRA_KEYS} --vault-password-file <(echo "$VAULT_PASS")
                        """
                    }
                }
            }
        }
		stage("Prepare build") {
			parallel{
				stage("Install consul configuration") {
					when {
						expression { params.INSTALL_CONSUL_CONFIGURATION }
					}
					steps {
						script {
							 withCredentials([string(credentialsId: 'ansible-vault-password', variable: 'VAULT_PASS')]) {
							  sh """
								${env.PREPARE_CMD} && \
								${env.ANSIBLE_EXEC_PLAYBOOK} ${env.BASE_ANSIBLE_PARAMS} ./configuration/deploy-consul-configuration.yml \
								${params.ANSIBLE_EXTRA_KEYS} --vault-password-file <(echo "$VAULT_PASS")
								"""
							 }
						}
					}
				}
				stage("Install keycloak configuration") {
					when {
						expression { params.INSTALL_KEYCLOAK_CONFIGURATION }
					}
					steps {
						script {
							 withCredentials([string(credentialsId: 'ansible-vault-password', variable: 'VAULT_PASS')]) {
							  sh """
								${env.PREPARE_CMD} && \
								${env.ANSIBLE_EXEC_PLAYBOOK} ${env.BASE_ANSIBLE_PARAMS} ./configuration/deploy-keycloak-configuration.yml \
								${params.ANSIBLE_EXTRA_KEYS} --vault-password-file <(echo "$VAULT_PASS")
								"""
							 }
						}
					}
				}
				stage("Install rabbit configuration") {
					when {
						expression { params.INSTALL_RABBIT_CONFIGURATION }
					}
					steps {
						script {
							 withCredentials([string(credentialsId: 'ansible-vault-password', variable: 'VAULT_PASS')]) {
							  sh """
								${env.PREPARE_CMD} && \
								${env.ANSIBLE_EXEC_PLAYBOOK} ${env.BASE_ANSIBLE_PARAMS} ./configuration/deploy-rabbitmq-configuration.yml \
								${params.ANSIBLE_EXTRA_KEYS} --vault-password-file <(echo "$VAULT_PASS")
								"""
							 }
						}
					}
				}
			}
		}
	}
}
